<?php

namespace Netgen\BlockManager\Collection\Result;

use Netgen\BlockManager\Item\ItemInterface;

/**
 * A result is a wrapper around the item generated by running the collection.
 * Instances of this object are wrapped into a result set, to be used by the block.
 */
final class Result
{
    /**
     * Defines a result generated from the collection item.
     */
    const TYPE_MANUAL = 0;

    /**
     * Defines a result generated from the item coming from the collection query.
     */
    const TYPE_DYNAMIC = 2;

    /**
     * @var int
     */
    private $position;

    /**
     * @var \Netgen\BlockManager\Item\ItemInterface
     */
    private $item;

    /**
     * @var \Netgen\BlockManager\Item\ItemInterface
     */
    private $subItem;

    public function __construct($position, ItemInterface $item, ItemInterface $subItem = null)
    {
        $this->position = $position;
        $this->item = $item;
        $this->subItem = $subItem;
    }

    public function getPosition()
    {
        return $this->position;
    }

    public function getItem()
    {
        return $this->item;
    }

    public function getSubItem()
    {
        return $this->subItem;
    }
}
